import { useState, useContext } from "react";
import ContactSearch from "./contactSearch";
import { useDispatch, useSelector } from "react-redux";
import { MessageSquare, UserPlus, Phone } from "react-feather";
import { Nav, NavLink, NavItem, TabContent, TabPane } from "reactstrap";
import ChatContext from "../../helpers/chatContext";
import { toogleChatBoard, setChatWith } from "../../redux/actions/index";
const ChatSection = () => {
  const dispatch = useDispatch();
  const atab = useSelector((state) => state.user.chatboard);
  const chatCtx = useContext(ChatContext);
  const chatMembers = chatCtx.chatMembers;
  const chats = chatCtx.chats;
  const currentUser = chatCtx.currentUser;
  const changeChat = chatCtx.changeChat;
  const createChat = chatCtx.createChat;
  const [activeTab, setActiveTab] = useState("chat");
  setActiveTab(atab);
  const ToggleTab = (tab) => {
    if (tab === "chat") {
      dispatch(toogleChatBoard("chat"));
    } else if (tab === "call") {
      dispatch(toogleChatBoard("call"));
    } else {
      dispatch(toogleChatBoard("contact"));
    }
  };

  const changeChatClick = (e, selectedUser) => {
    document.querySelectorAll(".chat-main li").forEach((item) => {
      item.classList.remove("active");
    });
    e.currentTarget.classList.add("active");
    const currentUserId = currentUser.id;

    const currentChat = chats.find(
      (x) =>
        x.users.includes(currentUserId) && x.users.includes(selectedUser.id)
    );

    if (currentChat) {
      changeChat(selectedUser.id);
    } else {
      createChat(currentUserId, selectedUser.id, chats);
    }
    document.querySelector(".sidebar-toggle").classList.add("mobile-menu");
  };

  const Tipin = (e) => {
    e.currentTarget.classList.toggle("pined");
  };

  return (
    <div className="chat custom-scroll">
      <div className="theme-title">
        <div className="media">
          <div>
            <h2>Chat</h2>
            <h4>Start New Conversation</h4>
          </div>
          <div className="media-body text-right"></div>
        </div>
      </div>
      <div className="theme-tab tab-sm chat-tabs logo-warpper">
        <Nav
          tabs
          id="myTab"
          role="tablist"
          style={{ borderBottom: "1px solid #eff1f2" }}
        >
          <NavItem>
            <NavLink
              className={`button-effect step4 ${
                activeTab === "chat" ? "active" : ""
              }`}
              onClick={() => ToggleTab("chat")}
              data-intro="Start chat"
            >
              <MessageSquare />
              Chat
            </NavLink>
          </NavItem>

          <NavItem>
            <NavLink
              className={`button-effect ${
                activeTab === "call" ? "active" : ""
              }`}
              onClick={() => ToggleTab("call")}
            >
              <Phone />
              call
            </NavLink>
          </NavItem>

          <NavItem>
            <NavLink
              className={`button-effect ${
                activeTab === "contact" ? "active" : ""
              }`}
              onClick={() => ToggleTab("contact")}
            >
              <UserPlus />
              Contact
            </NavLink>
          </NavItem>
        </Nav>

        <TabContent activeTab={activeTab}>
          <TabPane
            tabId="chat"
            className="fade show"
            id="chat"
            role="tabpanel"
            aria-labelledby="chat-tab"
          >
            <div className="theme-tab">
              <Nav tabs id="myTab1" role="tablist"></Nav>
              <TabContent>
                <ul className="chat-main">
                  {chatMembers.slice(0, 4).map((chatlist, i) => {
                    return (
                      <li
                        className={`${1 === chatlist.id ? "active" : ""}`}
                        key={i}
                        onClick={(e) => {
                          changeChatClick(e, chatlist);
                          dispatch(setChatWith(chatlist.name));
                          dispatch(toogleChatBoard("chat"));
                        }}
                      >
                        <div className="chat-box">
                          <div
                            className={`profile  ${chatlist.onlineStatus}`}
                            style={{
                              backgroundImage: `url('assets/images/${chatlist.thumb}')`,
                              backgroundSize: "cover",
                              backgroundPosition: "center",
                              display: "block",
                            }}
                          >
                            <img
                              className="bg-img"
                              src={`assets/images/${chatlist.thumb}`}
                              alt="Avatar"
                              style={{ display: "none" }}
                            />
                          </div>
                          <div className="details">
                            <h5>{chatlist.name}</h5>
                            <h6>{chatlist.mesg}</h6>
                          </div>
                          <div className="date-status">
                            <i
                              className="ti-pin2"
                              onClick={(e) => Tipin(e)}
                            ></i>
                            <h6>{chatlist.lastSeenDate}</h6>
                            {chatlist.status === "Sending" ||
                            "Failed" ||
                            "Seen" ? (
                              <h6
                                className={`${
                                  chatlist.status === "Sending"
                                    ? "font-dark"
                                    : chatlist.status === "Failed"
                                    ? "font-danger"
                                    : "font-success"
                                }  status`}
                              >
                                {chatlist.status === "8" ? "" : chatlist.status}
                              </h6>
                            ) : (
                              ""
                            )}
                            {chatlist.status === "8" ? (
                              <div className="badge badge-primary sm">
                                {chatlist.status}
                              </div>
                            ) : (
                              ""
                            )}
                          </div>
                        </div>
                      </li>
                    );
                  })}
                </ul>
              </TabContent>
            </div>
          </TabPane>
          <TabPane
            tabId="contact"
            className="fade show"
            id="contact"
            role="tabpanel"
            aria-labelledby="contact-tab"
          >
            <ContactSearch />
          </TabPane>
        </TabContent>
      </div>
    </div>
  );
};

export default ChatSection;
