import * as XMPP from "stanza";
let client;
export default function (username, password) {
  client = XMPP.createClient({
    jid: username,
    password,
    transports: {
      websocket: "ws://mongoose.mysmartpbx.org:5280/ws-xmpp",
      bosh: "false",
    },
  });
  client.on("session:started", () => {
    client.getRoster();
    client.sendPresence();
  });
  client.on("auth:failed", console.log("Failed"));
  client.on("auth:success", console.log("auth success"));
  // client.on("*", console.log);
  client.on("message", (msg) =>
    console.log("Message is as: " + JSON.stringify(msg))
  );
  this.client = client;
  return client;
}

export const connect = () => {
  client.connect();
};
