import React, { useEffect, useRef } from "react";
import { useContext } from "react";
import { Nav, TabContent } from "reactstrap";
import { setChatWith, unmountComponent } from "../../redux/actions/index";
import ChatContext from "../../helpers/chatContext";
import { useDispatch, useSelector } from "react-redux";
function chatedUsers() {
  const dispatch = useDispatch();
  const chatCtx = useContext(ChatContext);
  const chatMembers = chatCtx.chatMembers;
  const unmountComp = useSelector((state) => state.user.compuntUnmount);

  const changeChatClick = (e) => {
    document.querySelectorAll(".chat-main li").forEach((item) => {
      item.classList.remove("active");
    });
    e.currentTarget.classList.add("active");
    document.querySelector(".sidebar-toggle").classList.add("mobile-menu");
  };

  const Tipin = (e) => {
    e.currentTarget.classList.toggle("pined");
  };
  const checkHasMessages = () => {
    chatMembers.map((ci, i) => {
      if (!ci.hasOwnProperty("mesg")) {
        chatMembers.splice(i, 1);
      }
    });
  };
  useEffect(() => {
    checkHasMessages();
  }, [unmountComp]);

  const isMounted = useRef(false);

  useEffect(() => {
    if (isMounted.current) {
      document.querySelectorAll(".chat-main li").forEach((item) => {
        item.classList.remove("active");
      });
      var v = document.querySelectorAll(".chat-main li")[0];
      v.classList.add("active");
      document.querySelector(".sidebar-toggle").classList.add("mobile-menu");

      console.log("v", v);
    } else {
      isMounted.current = true;
    }
  }, [chatMembers]);

  return (
    <div className="theme-tab">
      <Nav tabs id="myTab1" role="tablist"></Nav>
      <TabContent>
        <ul className="chat-main">
          {chatMembers.slice(0, 4).map((chatlist, i) => {
            return (
              <li
                className={`${1 === chatlist.id ? "active" : ""}`}
                key={i}
                onClick={(e) => {
                  changeChatClick(e, chatlist);
                  dispatch(setChatWith(chatlist.first_name));
                  i > 0 ? dispatch(unmountComponent({ name: i })) : null;
                }}
              >
                <div className="chat-box">
                  <div
                    className={`profile  ${chatlist.onlineStatus}`}
                    style={{
                      backgroundImage: `url('assets/images/${chatlist.thumb}')`,
                      backgroundSize: "cover",
                      backgroundPosition: "center",
                      display: "block",
                    }}
                  >
                    <img
                      className="bg-img"
                      src={`assets/images/${chatlist.thumb}`}
                      alt="Avatar"
                      style={{ display: "none" }}
                    />
                  </div>
                  <div className="details">
                    <h5>{chatlist.first_name}</h5>
                    <h6>{chatlist.mesg}</h6>
                  </div>
                  <div className="date-status">
                    <i className="ti-pin2" onClick={(e) => Tipin(e)}></i>
                    <h6>{chatlist.lastSeenDate}</h6>
                    {chatlist.status === "Sending" || "Failed" || "Seen" ? (
                      <h6
                        className={`${
                          chatlist.status === "Sending"
                            ? "font-dark"
                            : chatlist.status === "Failed"
                            ? "font-danger"
                            : "font-success"
                        }  status`}
                      >
                        {chatlist.status === "8" ? "" : chatlist.status}
                      </h6>
                    ) : (
                      ""
                    )}
                    {chatlist.status === "8" ? (
                      <div className="badge badge-primary sm">
                        {chatlist.status}
                      </div>
                    ) : (
                      ""
                    )}
                  </div>
                </div>
              </li>
            );
          })}
        </ul>
      </TabContent>
    </div>
  );
}

export default chatedUsers;
