import React, { useState } from "react";
import { useRouter } from "next/router";
import { toast } from "react-toastify";
import { Spinner } from "reactstrap";
import { useDispatch } from "react-redux";
import { Alert } from "reactstrap";
import { setUser } from "../../../redux/actions/index";
const URL = process.env.NEXT_PUBLIC_REACT_APP_WEBSOCKET_ENDPOINT;
const DOMAIN = process.env.NEXT_PUBLIC_REACT_APP_DEFAULT_DOMAIN;
const { client } = require("@xmpp/client");
const debug = require("@xmpp/debug");
let clientXmpp = client({
  service: URL,
  domain: DOMAIN,
  resource: "WebApp-login",
  username: "",
  password: "",
  transport: "websocket",
});

const Auth_SignIn = (props) => {
  const dispatch = useDispatch();
  let errorArray = [];
  let error;
  const [credential, setCredential] = useState({
    username: "",
    password: "",
    accountName: "",
    errors: [],
    loading: false,
  });
  const router = useRouter();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredential({ ...credential, [name]: value });
  };

  // simple  login
  const userLogin = async (event) => {
    event.preventDefault();
    if (isFormValid()) {
      setCredential({ ...credential, errors: [], loading: true });
      doConnection();
    } else {
      console.log("not valid");
    }
  };
  const doConnection = () => {
    /** Here  we check if the user is not then setup connection in else we simply log */
    if (clientXmpp.status !== "online") {
      clientXmpp = client({
        service: URL,
        domain: DOMAIN,
        resource: "chitChat",
        username: credential.username,
        password: credential.password,
        transport: "websocket",
      });

      debug(clientXmpp, true);

      //connecting user to mongooseIm server
      clientXmpp.on("error", (error) => {
        console.log("Log", error.message);
      });
      clientXmpp.start().catch((error) => {
        console.log("Error\n", error.message);
        error = {
          message:
            error.message === "not-authorized"
              ? "Invalid Username or Password"
              : error.message,
        };
        setCredential({ ...credential, errors: errorArray.concat(error) });
        clientXmpp.stop();
      });

      //Here we check status if user status is online the we save user information and send to messenger service
      //because we connecting user to mongooseIm it will take some time to get online status
      clientXmpp.on("status", (status) => {
        if (status === "online") {
          dispatch(setUser(credential.username));
          clientXmpp.stop();
          router.push("/messenger");
        }
      });
    } else {
      console.log(`${credential.username} - is online`);
    }
  };

  const isFormValid = () => {
    if (isFormEmpty(credential.username, credential.password)) {
      error = { message: "Please Fill all the Fields" };
      setCredential({ ...credential, errors: errorArray.concat(error) });
      return false;
    } else {
      return true;
    }
  };

  const isFormEmpty = (username, password) => {
    return !username.length && !password.length;
  };

  const displayErrors = () =>
    credential.errors.map((error, i) => (
      <Alert key={i} color="danger">
        {error.message}
      </Alert>
    ));
  const redirectToSignUpPage = () => {
    console.log("signup");
    router.push("/auth/signUp");
  };

  return (
    <div className="login-page1">
      <div className="container-fluid p-0">
        <div className="row m-0">
          <div className="col-12 p-0">
            <div className="login-contain-main">
              <div className="left-page">
                <div className="login-content">
                  <div className="login-content-header">
                    <a href="/landing">
                      <img
                        className="image-fluid"
                        src="/assets/images/logo/landing-logo.png"
                        alt="images"
                      />
                    </a>
                  </div>
                  <h3>Hello Everyone , We are Chitchat</h3>
                  <h4>Welcome to chitchat please login to your account.</h4>
                  {credential.errors.length > 0 && displayErrors()}
                  <form className="form1">
                    <div className="form-group">
                      <label
                        className="col-form-label"
                        htmlFor="inputusername3"
                      >
                        Username
                      </label>
                      <input
                        className="form-control"
                        id="inputusername3"
                        onChange={(e) => handleChange(e)}
                        name="username"
                        value={credential.username}
                        type="username"
                        placeholder="Demo@123gmail.com"
                      />
                    </div>
                    <div className="form-group">
                      <label
                        className="col-form-label"
                        htmlFor="inputPassword3"
                      >
                        Password
                      </label>
                      <span> </span>
                      <input
                        className="form-control"
                        id="inputPassword3"
                        onChange={(e) => handleChange(e)}
                        name="password"
                        type="password"
                        value={credential.password}
                        placeholder="*******"
                      />
                    </div>
                    <div className="form-group">
                      <label className="col-form-label" htmlFor="accountName">
                        Account Name
                      </label>
                      <span> </span>
                      <input
                        className="form-control"
                        id="accountName"
                        onChange={(e) => handleChange(e)}
                        name="accountName"
                        type="text"
                        value={credential.accountName}
                        placeholder="account name"
                      />
                    </div>
                    <div className="form-group">
                      <div className="rememberchk">
                        <div className="input-text form-check pl-0">
                          <input
                            type="checkbox"
                            id="gridCheck1"
                            aria-label="Checkbox for following text input"
                          />
                          <label
                            className="form-check-label ml-2 mr-auto"
                            htmlFor="gridCheck1"
                          >
                            Remember Me.
                          </label>
                          <h6>Forgot Password?</h6>
                        </div>
                      </div>
                    </div>
                    <div className="form-group">
                      <div className="buttons">
                        <button
                          disabled={credential.loading}
                          className="btn btn-primary button-effect"
                          onClick={userLogin}
                          type="submit"
                        >
                          {credential.loading && (
                            <Spinner color="dark" size="sm" />
                          )}
                          Login
                        </button>
                        <button
                          className="btn button-effect btn-signup"
                          onClick={() => redirectToSignUpPage()}
                        >
                          SignUp
                        </button>
                      </div>
                    </div>
                  </form>
                  <div className="line">
                    <h6>OR Connect with</h6>
                  </div>
                  <div className="medialogo">
                    <ul>
                      <li>
                        <a
                          className="icon-btn btn-danger button-effect"
                          href="#"
                        >
                          <i className="fa fa-google"></i>
                        </a>
                      </li>
                      <li>
                        <a
                          className="icon-btn btn-primary button-effect"
                          href="#"
                        >
                          <i className="fa fa-twitter"></i>
                        </a>
                      </li>
                      <li>
                        <a
                          className="icon-btn btn-facebook button-effect"
                          href="#"
                        >
                          <i className="fa fa-facebook-f"></i>
                        </a>
                      </li>
                    </ul>
                  </div>
                  <div className="termscondition">
                    <h4 className="mb-0">
                      <span>*</span>Terms and condition<b>&amp;</b>Privacy
                      policy
                    </h4>
                  </div>
                </div>
              </div>
              <div className="right-page">
                <div className="right-login animat-rate">
                  <div className="animation-block">
                    <div className="bg_circle">
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                      <div></div>
                    </div>
                    <div className="cross"></div>
                    <div className="cross1"></div>
                    <div className="cross2"></div>
                    <div className="dot"></div>
                    <div className="dot1"></div>
                    <div className="maincircle"></div>
                    <div className="top-circle"></div>
                    <div className="center-circle"></div>
                    <div className="bottom-circle"></div>
                    <div className="bottom-circle1"></div>
                    <div className="right-circle"></div>
                    <div className="right-circle1"></div>
                    <img
                      className="heart-logo"
                      src="/assets/images/login_signup/5.png"
                      alt="login logo"
                    />
                    <img
                      className="has-logo"
                      src="/assets/images/login_signup/4.png"
                      alt="login logo"
                    />
                    <img
                      className="login-img"
                      src="/assets/images/login_signup/1.png"
                      alt="login logo"
                    />
                    <img
                      className="boy-logo"
                      src="/assets/images/login_signup/6.png"
                      alt="login boy logo"
                    />
                    <img
                      className="girl-logo"
                      src="/assets/images/login_signup/7.png"
                      alt="girllogo"
                    />
                    <img
                      className="cloud-logo"
                      src="/assets/images/login_signup/2.png"
                      alt="login logo"
                    />
                    <img
                      className="cloud-logo1"
                      src="/assets/images/login_signup/2.png"
                      alt="login logo"
                    />
                    <img
                      className="cloud-logo2"
                      src="/assets/images/login_signup/2.png"
                      alt="login logo"
                    />
                    <img
                      className="cloud-logo3"
                      src="/assets/images/login_signup/2.png"
                      alt="login logo"
                    />
                    <img
                      className="cloud-logo4"
                      src="/assets/images/login_signup/2.png"
                      alt="login logo"
                    />
                    <img
                      className="has-logo1"
                      src="/assets/images/login_signup/4.png"
                      alt="login logo"
                    />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Auth_SignIn;
