import * as XMPP from "stanza";
import onAuthFailed from "onAuthFailed";
import onAuthSuccess from "onAuthSuccess";
const chatClient = {
  create: (create = (username, password) => {
    const client = XMPP.createClient({
      jid: username,
      password,
      transports: {
        websocket: "ws://mongoose.mysmartpbx.org:5280/ws-xmpp",
        bosh: "false",
      },
    });
    client.on("session:started", () => {
      client.getRoster();
      client.sendPresence();
    });
    client.on("auth:failed", onAuthFailed);
    client.on("auth:success", onAuthSuccess);
    // client.on("*", console.log);
    client.on("message", this.onMessage);
    client.on("message", (msg) =>
      console.log("Message is as: " + JSON.stringify(msg))
    );
    this.client = client;
    return client;
  }),

  connect: (connect = () => {
    this.client.connect();
  }),
};

// disconnect() {
//   this.client.disconnect();
// }

// const send(message) {
//   this.client.sendMessage(message);
// }
export default chatClient;
