import * as XMPP from "stanza";

export const create = (username, password) => {
  const client = XMPP.createClient({
    jid: username,
    password,
    transports: {
      websocket: "ws://mongoose.mysmartpbx.org:5280/ws-xmpp",
      bosh: "false",
    },
  });
  client.on("session:started", () => {
    client.getRoster();
    client.sendPresence();
  });
  client.on("auth:failed", this.onAuthFailed);
  client.on("auth:success", this.onAuthSuccess);
  // client.on("*", console.log);
  client.on("message", this.onMessage);
  client.on("message", (msg) =>
    console.log("Message is as: " + JSON.stringify(msg))
  );
  this.client = client;
  return client;
};

export const connect = () => {
  this.client.connect();
};

