import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useState, useContext } from "react";
import Cookies from "universal-cookie";
import { FormGroup, Input } from "reactstrap";
import ChatContext from "../../helpers/chatContext";
import { Nav, TabContent } from "reactstrap";
import axios from "../../api/index";

import {
  fetchUsers,
  setChatWith,
  setActiveTab,
  chatededUsers,
} from "../../redux/actions/index";
const id = "fbdcd3c94af9ffaa12f0ff4308d3b9bd";
const cookies = new Cookies();
const cokie = cookies.get("credentials");
function form() {
  const dispatch = useDispatch();
  const cookies = new Cookies();
  const cokie = cookies.get("credentials");
  const accountId = useSelector((state) => state.user.accountId);
  const usersP = useSelector((state) => state.user.users);
  const [searchTerm, setSearch] = useState(null);
  const chatCtx = useContext(ChatContext);
  const chatMembers = chatCtx.chatMembers;

  const chats = chatCtx.chats;
  const currentUser = chatCtx.currentUser;
  const changeChat = chatCtx.changeChat;
  const createChat = chatCtx.createChat;
  const [chatSubTab, setChatSubTab] = useState("contacts");

  useEffect(() => {
    dispatch(fetchUsers());
  }, []);

  const changeChatClick = (e, selectedUser) => {
    document.querySelectorAll(".chat-main li").forEach((item) => {
      item.classList.remove("active");
    });
    e.currentTarget.classList.add("active");
    const currentUserId = currentUser.id;

    const currentChat = chats.find(
      (x) =>
        x.users.includes(currentUserId) && x.users.includes(selectedUser.id)
    );

    if (currentChat) {
      changeChat(selectedUser.id);
    } else {
      createChat(currentUserId, selectedUser.id, chats);
    }
    document.querySelector(".sidebar-toggle").classList.add("mobile-menu");
  };

  return (
    <form>
      <FormGroup>
        <Input
          type="email"
          name="email"
          type="search"
          placeholder="Search"
          onChange={(e) => setSearch(e.target.value)}
        />
      </FormGroup>
      <div className="theme-tab">
        <Nav tabs id="myTab1" role="tablist"></Nav>
        <TabContent activeTab={chatSubTab}>
          <ul className="chat-main">
            {usersP.length > 0 &&
              usersP[0]
                .filter((data) => {
                  if (searchTerm == null) {
                    return data;
                  } else if (
                    data.first_name
                      .toLowerCase()
                      .includes(searchTerm.toLowerCase())
                  ) {
                    return data;
                  }
                })
                .map((chatlist, i) => {
                  return (
                    <li
                      className={`${1 === chatlist.id ? "active" : ""}`}
                      key={i}
                      onClick={(e) => {
                        changeChatClick(e, chatlist);
                        dispatch(setChatWith(chatlist.first_name));
                        dispatch(setActiveTab("chat"));
                      }}
                    >
                      <div className="chat-box">
                        <div
                          className={`profile  ${chatlist.last_name}`}
                          style={{
                            backgroundImage: `url('assets/images/${chatlist.thumb}')`,
                            backgroundSize: "cover",
                            backgroundPosition: "center",
                            display: "block",
                          }}
                        >
                          <img
                            className="bg-img"
                            src={`assets/images/${chatlist.thumb}`}
                            alt="Avatar"
                            style={{ display: "none" }}
                          />
                        </div>
                        <div className="details">
                          <h5>{chatlist.first_name}</h5>
                          <h6>Team Leader</h6>
                        </div>
                        <div className="date-status">{chatlist.last_name}</div>
                      </div>
                    </li>
                  );
                })}
          </ul>
        </TabContent>
      </div>
    </form>
  );
}

export default form;
