import { useState, useContext } from "react";
import ContactSearch from "./contactSearch";
import { MessageSquare, UserPlus } from "react-feather";
import { Nav, NavLink, NavItem, TabContent, TabPane } from "reactstrap";
import ChatContext from "../../helpers/chatContext";

const ChatSection = () => {
  const chatCtx = useContext(ChatContext);
  const chatMembers = chatCtx.chatMembers;
  const chats = chatCtx.chats;
  const currentUser = chatCtx.currentUser;
  const changeChat = chatCtx.changeChat;
  const createChat = chatCtx.createChat;
  const [activeTab, setActiveTab] = useState("chat");

  const ToggleTab = (tab) => {
    setActiveTab(tab);
  };

  const changeChatClick = (e, selectedUser) => {};

  const Tipin = (e) => {
    e.currentTarget.classList.toggle("pined");
  };

  return (
    <div className="chat custom-scroll">
      <div className="theme-title">
        <div className="media">
          <div>
            <h2>Chat</h2>
            <h4>Start New Conversation</h4>
          </div>
          <div className="media-body text-right"></div>
        </div>
      </div>
      <div className="theme-tab tab-sm chat-tabs logo-warpper">
        <Nav
          tabs
          id="myTab"
          role="tablist"
          style={{ borderBottom: "1px solid #eff1f2" }}
        >
          <NavItem>
            <NavLink
              className={`button-effect step4 ${
                activeTab === "chat" ? "active" : ""
              }`}
              onClick={() => ToggleTab("chat")}
              data-intro="Start chat"
            >
              <MessageSquare />
              Chat
            </NavLink>
          </NavItem>

          <NavItem>
            <NavLink
              className={`button-effect ${
                activeTab === "call" ? "active" : ""
              }`}
              onClick={() => ToggleTab("call")}
            >
              <UserPlus />
              Contact
            </NavLink>
          </NavItem>
        </Nav>

        <TabContent activeTab={activeTab}>
          <TabPane
            tabId="chat"
            className="fade show"
            id="chat"
            role="tabpanel"
            aria-labelledby="chat-tab"
          >
            <ContactSearch />
          </TabPane>
          <TabPane
            tabId="call"
            className="fade show"
            id="call"
            role="tabpanel"
            aria-labelledby="call-tab"
          >
            <ContactSearch />
          </TabPane>
        </TabContent>
      </div>
    </div>
  );
};

export default ChatSection;
