import React from "react";
import styles from "./styles.module.css";
import { useState, useContext } from "react";
import { FormGroup, Input } from "reactstrap";
import ChatContext from "../../helpers/chatContext";
import { Phone, ArrowDownLeft } from "react-feather";
function form() {
  const [searchTerm, setSearch] = useState(null);
  const chatCtx = useContext(ChatContext);
  const chatMembers = chatCtx.chatMembers;
  return (
    <form>
      <FormGroup>
        <Input
          type="email"
          name="email"
          type="search"
          placeholder="Search"
          onChange={(e) => setSearch(e.target.value)}
        />
      </FormGroup>
      {chatMembers
        .filter((data) => {
          if (searchTerm == null) {
            return data;
          } else if (
            data.name.toLowerCase().includes(searchTerm.toLowerCase())
          ) {
            return data;
          }
        })
        .map((data) => (
          <ul className="call-log-main">
            <li className="active">
              <div className="call-box">
                <div
                  className="profile offline"
                  style={{
                    backgroundImage: `url('assets/images/contact/2.jpg')`,
                    backgroundSize: "cover",
                    backgroundPosition: "center",
                    display: "block",
                  }}
                ></div>
                <div className="details">
                  <h5> {data.name}</h5>
                  <h6>
                    <ArrowDownLeft />
                    3:30 pm
                  </h6>
                </div>
                <div className="call-status">
                  <div className="icon-btn btn-outline-success button-effect btn-sm">
                    <Phone />
                  </div>
                </div>
              </div>
            </li>
          </ul>
        ))}
    </form>
  );
}

export default form;
